INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2013-08-26 20:12:31', '2013-08-26 20:12:31', '<a href="http://localhost/blog/wp-content/uploads/2013/08/Desert.jpg"><img class="alignnone size-medium wp-image-7" alt="Desert" src="http://localhost/blog/wp-content/uploads/2013/08/Desert-300x225.jpg" width="300" height="225" /></a>\r\n\r\nBienvenido a WordPress. Esta es tu primera entrada. Edítala o bórrala, ¡y comienza a publicar!.', '¡Hola mundo!', '', 'publish', 'open', 'open', '', 'hola-mundo', '', '', '2013-08-26 21:23:24', '2013-08-26 21:23:24', '', 0, 'http://localhost/blog/?p=1', 0, 'post', '', 1),
(2, 1, '2013-08-26 20:12:31', '2013-08-26 20:12:31', 'Esto es una página de ejemplo. Es diferente a una entrada porque permanece fija en un lugar y se mostrará en la navegación de tu sitio (en la mayoría de los temas). La mayoría de la gente empieza con una página de Acerca de, que les presenta a los potenciales visitantes del sitio. Podría ser algo como esto::\n\n<blockquote>¡Hola! Soy mensajero por el día, aspirante a actor por la noche, y este es mi blog. Vivo en Madrid, tengo un perrazo llamado Duque y me gustan las piñas coladas (y que me pille un chaparrón)</blockquote>\n\n...o algo así:\n\n<blockquote>La empresa XYZ se fundó en 1971 y ha estado ofreciendo "cosas" de calidad al público desde entonces. Situada en Madrid, XYZ emplea a más de 2.000 personas y hace todo tipo de cosas sorprendentes para la comunidad de Madrid.</blockquote>\n\nSi eres nuevo en WordPress deberías ir a <a href="http://localhost/blog/wp-admin/">tu escritorio</a> para borrar esta páginay crear páginas nuevas con tu propio contenido. ¡Pásalo bien!', 'Página de ejemplo', '', 'publish', 'open', 'open', '', 'pagina-ejemplo', '', '', '2013-08-26 20:12:31', '2013-08-26 20:12:31', '', 0, 'http://localhost/blog/?page_id=2', 0, 'page', '', 0),
(3, 1, '2013-08-26 20:13:01', '0000-00-00 00:00:00', '', 'Borrador automático', '', 'auto-draft', 'open', 'open', '', '', '', '', '2013-08-26 20:13:01', '0000-00-00 00:00:00', '', 0, 'http://localhost/blog/?p=3', 0, 'post', '', 0),
